import axios from "axios";

export const createRecurringCharge = async ({ merchantId, productId, token }) => {

    try {
        const response = await axios.post(`https://api.tryappollo.com/billing/recurring-charge/${productId}?merchantId=${merchantId}`,
            {},
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.checkoutUrl
    } catch (error) {
        return error.response.data
    }

}
