import { createRecurringCharge } from "./createRecurringCharge.js"
import { createSingleCharge } from "./createSingleCharge.js"

class Billings {
    constructor() {

    }

    async createSingleCharge(merchantId, productId) {
        if (!this.checkTokenExpiry()) {
            await this.getAccessToken()
        }
        const token = this.token.access_token

        const checkoutUrl = await createSingleCharge({ merchantId, productId, token })
        return checkoutUrl
    }

    async createRecurringCharge(merchantId, productId) {
        if (!this.checkTokenExpiry()) {
            await this.getAccessToken()
        }
        const token = this.token.access_token

        const checkoutUrl = await createRecurringCharge({ merchantId, productId, token })
        return checkoutUrl
    }

}

export default Billings