import axios from "axios";

export const removeProductsFromCollection = async ({ merchantId, payload, token, collectionId }) => {

    try {
        const response = await axios.post(`https://api.tryappollo.com/collections/${collectionId}/add?merchantId=${merchantId}`,
            {
                products: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.collection
    } catch (error) {
        return error.response.data
    }

}

