import axios from "axios";

export const createCollection = async ({ merchantId, payload, token }) => {

    try {
        const response = await axios.post(`https://api.tryappollo.com/collections/?merchantId=${merchantId}`,
            {
                collection: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.collection
    } catch (error) {
        return error.response.data
    }

}

