import axios from "axios";

export const getCollection = async ({ merchantId, collectionId, token }) => {

    try {
        const response = await axios.get(`https://api.tryappollo.com/collections/${collectionId}?merchantId=${merchantId}`,
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.collection
    } catch (error) {
        return error.response.data
    }

}

