import axios from "axios";

export const updateCollection = async ({ merchantId, collectionId, payload, token }) => {

    try {
        const response = await axios.patch(`https://api.tryappollo.com/collections/${collectionId}?merchantId=${merchantId}`,
            {
                collection: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.collection
    } catch (error) {
        return error.response.data
    }

}
