import axios from "axios";

export const createCustomer = async ({ merchantId, payload, token }) => {
    try {
        const response = await axios.post(`https://api.tryappollo.com/customers?merchantId=${merchantId}`,
            {
                customer: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.customer
    } catch (error) {
        return error.response.data
    }

}
