import axios from "axios";

export const getCustomer = async ({ merchantId, customerId, token }) => {

    try {
        const response = await axios.get(`https://api.tryappollo.com/customers/${customerId}?merchantId=${merchantId}`,
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.customer
    } catch (error) {
        return error.response.data
    }

}
