import axios from "axios";

export const getCustomers = async ({ merchantId, limit, page, token }) => {

    try {
        const queryString = `merchantId=${merchantId}${limit ? `&limit=${limit}` : ""}${page ? `&page=${page}` : ""}`

        const response = await axios.get(`https://api.tryappollo.com/customers?${queryString}`,
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.customers
    } catch (error) {
        return error.response.data
    }

}
