import axios from "axios";

export const updateCustomer = async ({ merchantId, customerId, payload, token }) => {

    try {
        const response = await axios.patch(`https://api.tryappollo.com/customers/${customerId}?merchantId=${merchantId}`,
            {
                customer: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.customer
    } catch (error) {
        return error.response.data
    }

}
