import axios from "axios";

export const createOrder = async ({ merchantId, payload, token }) => {

    try {
        const response = await axios.post(`https://api.tryappollo.com/orders?merchantId=${merchantId}`,
            {
                order: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.order
    } catch (error) {
        return error.response.data
    }
}

