import axios from "axios";

export const getOrder = async ({ merchantId, orderId, token }) => {

    try {
        const response = await axios.get(`https://api.tryappollo.com/orders/${orderId}?merchantId=${merchantId}`,
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.order
    } catch (error) {
        return error.response.data
    }

}
