import axios from "axios";

export const getOrders = async ({ merchantId, limit, page, token }) => {


    try {
        const queryString = `merchantId=${merchantId}${limit ? `&limit=${limit}` : ""}${page ? `&page=${page}` : ""}`

        const response = await axios.get(`https://api.tryappollo.com/orders?${queryString}`,
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.orders
    } catch (error) {
        return error.response.data
    }

}
