import axios from "axios";

export const updateOrder = async ({ merchantId, orderId, payload, token }) => {
    try {
        const response = await axios.patch(`https://api.tryappollo.com/orders/${orderId}?merchantId=${merchantId}`,
            {
                order: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.order
    } catch (error) {

        return error.response.data
    }

}
