import axios from "axios";

export const createProduct = async ({ merchantId, payload, token }) => {

    try {
        const response = await axios.post(`https://api.tryappollo.com/products?merchantId=${merchantId}`,
            {
                product: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.product
    } catch (error) {
        return error.response.data
    }

}
