import axios from "axios";

export const getProduct = async ({ merchantId, productId, token }) => {

    try {
        const response = await axios.get(`https://api.tryappollo.com/products/${productId}?merchantId=${merchantId}`, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })

        // console.log(response.data);

        return response.data.product
    } catch (error) {
        return error.response.data
    }

}
