import axios from "axios";

export const getProducts = async ({ merchantId, limit, page, token }) => {

    const queryString = `merchantId=${merchantId}${limit ? `&limit=${limit}` : ""}${page ? `&page=${page}` : ""}`

    try {
        const response = await axios.get(`https://api.tryappollo.com/products?${queryString}`, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })

        return response.data.products

    } catch (error) {
        return error.response.data
    }

}
