import axios from "axios";

export const updateProduct = async ({ merchantId, payload, productId, token }) => {

    try {
        const response = await axios.patch(`https://api.tryappollo.com/products/${productId}?merchantId=${merchantId}`,
            {
                product: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.order
    } catch (error) {
        return error.response.data
    }

}
