import axios from "axios";

export const createVariant = async ({ merchantId, payload, productId, token }) => {

    try {
        const response = await axios.post(`https://api.tryappollo.com/${productId}/variants?merchantId=${merchantId}`,
            {
                variant: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })

        return response.data.variant
    } catch (error) {
        return error.response.data
    }

}
