import axios from "axios";

export const getVariant = async ({ merchantId, productId, variantId, token }) => {

    try {
        const response = await axios.get(`https://api.tryappollo.com/${productId}/variants/${variantId}?merchantId=${merchantId}`, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })

        return response.data.variant
    } catch (error) {
        return error.response.data
    }

}
