import axios from "axios";

export const getVariants = async ({ merchantId, productId, token, limit, page }) => {

    try {
        const queryString = `merchantId=${merchantId}${limit ? `&limit=${limit}` : ""}${page ? `&page=${page}` : ""}`

        const response = await axios.get(`https://api.tryappollo.com/${productId}/variants?${queryString}`, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })

        return response.data.variants
    } catch (error) {
        return error.response.data
    }

}
