import axios from "axios";

export const updateVariant = async ({ merchantId, payload, productId, variantId, token }) => {

    try {
        const response = await axios.patch(`https://api.tryappollo.com/${productId}/variants/${variantId}?merchantId=${merchantId}`,
            {
                variant: payload
            },
            {
                headers: {
                    Authorization: `Bearer ${token}`,
                }
            })

        return response.data.variant
    } catch (error) {
        return error.response.data
    }

}
